drop database if exists ac1_pais_minerio;
create database ac1_pais_minerio;
use ac1_pais_minerio;

create table continente (
	id				int				primary key			auto_increment,
	nome			varchar(30)		not null,
	area			int				check (area>0),
	quant_vulc		int				check (quant_vulc>0)
);

create table pais (
	id				int				primary key			auto_increment,
	nome			varchar(30)		not null,
	capital			varchar(30)		not null,
	moeda			varchar(30)		not null,
	continente_id	int				references continente(id)
);

create table minerio (
	id				int				primary key			auto_increment,
	nome			varchar(30)		not null
	
);

create table prod_minerio (
	pais_id				int				references pais(id),
	minerio_id			int				references minerio(id),
    quant				int				check (quant>0),
    ano					int				check (ano>0)
);

